<?php
use App\Entity\Artist;
use App\Entity\Model;

$data = str_replace(" ", "", $data);
// Reussir Ã  mettre se code dans controleur
$ch = curl_init();
curl_setopt($ch, CURLOPT_URL, "https://api.spotify.com/v1/search?q=".$data."&type=artist");
curl_setopt($ch, CURLOPT_HTTPHEADER, array('Authorization: Bearer ' . $_SESSION['token'] ));
curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
$result = curl_exec($ch);

if($result === null){
    header('Location: http://localhost:8000/main/form/');
}

$arr = json_decode($result)->artists->items;
$artists = [];
foreach ($arr as $element) {
    if($element?->images)
    {
        $artists[] =  new Artist($element->id, $element->name, $element->images[0]->url, $element->followers->total, $element->external_urls->spotify);

    }
    else{
        $artists[] =  new Artist($element->id, $element->name, "data:image/jpeg;base64,", $element->followers->total, $element->external_urls->spotify);

    }
}
curl_close($ch);

echo "<div style='display:flex;'> ";
foreach ($artists as $artist){
    echo "<div style='display:flex;flex-direction: column;'> ";
    echo '<form action="/artist/favArtist/'.$artist->getId().'/'.$data.'" method="post"><button type="submit">Mise en fav</button></form>' ;
    echo "<a href='/Artist/info/".$artist->getId()."'>".$artist->display()."</a>";
    echo "</div>";
}
echo "</div>";


